import Header from "./components/content/header";
import Footer from "./components/content/footer";
import Body from "./components/content/body";
import { Route, BrowserRouter as Router, Switch } from "react-router-dom";

import React, { Component } from "react";
import RegisterForm from "./components/actions/registerForm";
import LoginForm from "./components/actions/loginForm";
import Logout from "./components/actions/logout";

import UserContext from "./context/userContext";
import DarkContext from "./context/darkMode";
import PostDetail from "./components/content/postDetail";

import apiURL from "./apiUrl";
import axios from "axios";

export default class App extends Component {
  state = {
    currentUser: { name: "" },
    users: [],
    darkMode: false,
  };

  handleLoggedIn = (username) => {
    const user = { name: username };
    this.setState({ currentUser: user });
  };

  componentDidMount() {
    axios
      .get(apiURL + "user/list/")
      .then((res) => this.setState({ users: res.data }));
  }

  getAuthorById = (pk) => {
    console.log("Got user for " + pk);
    return this.state.users.find((user) => user.id === pk).username;
  };

  toggleDarkMode = () => {
    console.log(document.querySelector("p"));
    this.setState({ darkMode: !this.state.darkMode });
  };

  render() {
    return (
      <DarkContext.Provider
        value={{
          darkMode: this.state.darkMode,
          toggleDarkMode: this.toggleDarkMode,
          background: "dark-bg",
        }}
      >
        <UserContext.Provider
          value={{
            currentUser: this.state.currentUser,
            onLogIn: this.handleLoggedIn,
            getAuthor: this.getAuthorById,
          }}
        >
          <Router>
            <Header />
            <Switch>
              <Route
                path="/post/:pk"
                render={(props) => <PostDetail {...props} />}
              />

              <Route
                path="/login"
                render={(props) => <LoginForm {...props} />}
              />
              <Route
                path="/register"
                render={(props) => <RegisterForm {...props} />}
              />
              <Route path="/logout" render={(props) => <Logout {...props} />} />

              <Route exact path="/" component={Body} />
            </Switch>
            <Footer />
          </Router>
        </UserContext.Provider>
      </DarkContext.Provider>
    );
  }
}
