import React, { useContext } from "react";
import axios from "axios";
import apiURL from "../../apiUrl";
import { useEffect, useState } from "react";
import DarkContext from "../../context/darkMode";
import UserContext from "../../context/userContext";

const SearchPage = () => {
  const [posts, setPosts] = useState([]);
  const darkContext = useContext(DarkContext);
  const userContext = useContext(UserContext);

  useEffect(() => {
    axios.get(apiURL + "search/" + window.location.search).then((res) => {
      setPosts(res.data);
      console.log(res.data);
    });
  }, [setPosts]);

  /*<React.Fragment>
      {posts.length ? (
        <div className="posts-section">
          {posts.map((post) => {
            const { title, id } = post;
            return <div key={id}>{title}</div>;
          })}
        </div>
      ) : (
        <div className="no-results border">
          <h3>No Results</h3>
        </div>
      )}
    </React.Fragment> */

  return (
    <React.Fragment>
      {posts.length ? (
        <div
          className={
            darkContext.darkMode
              ? "posts-results-section dark-box-shadow text-light"
              : "posts-results-section border"
          }
        >
          {posts.map((post) => {
            const { title, id, author, published, excerpt } = post;
            return (
              <div className="post" key={id}>
                <h3> {title}</h3>

                <div>
                  <p className="author">
                    {author ? userContext.getAuthor(post.author) : "Anonymous"}
                  </p>

                  <p className="date">
                    <i class="fas fa-grip-lines-vertical"></i>
                    {new Intl.DateTimeFormat("en-GB", {
                      dateStyle: "full",
                    }).format(new Date(published))}
                  </p>
                </div>
                <hr />
                <p>{excerpt.substring(0, 150) + "..."}</p>
              </div>
            );
          })}
        </div>
      ) : (
        <div
          className={
            +darkContext.darkMode
              ? "no-results border bg-dark text-light"
              : "no-results border bg-light"
          }
        >
          <h3>No Results</h3>
        </div>
      )}
    </React.Fragment>
  );
};

export default SearchPage;
