import React from "react";
import Skeleton from "@yisheng90/react-loading";

/*<Spinner animation="grow" variant="primary" />*/

const PostLoading = (Component) => {
  const renderLoadingBars = (numRows) => {
    switch (numRows) {
      case 1:
        return <Skeleton height={30} rows={5} width={"90%"} />;
      case 2:
        return (
          <React.Fragment>
            <Skeleton height={250} width={"90%"} />
            <Skeleton height={250} width={"90%"} />
          </React.Fragment>
        );

      case 3:
        return (
          <React.Fragment>
            <Skeleton height={250} width={"90%"} />
            <Skeleton height={250} width={"90%"} />
            <Skeleton height={250} width={"90%"} />
          </React.Fragment>
        );
    }
  };

  return function PostLoadingComponent({ rows, isLoading, ...props }) {
    if (!isLoading) {
      return <Component {...props} />;
    }

    return <React.Fragment>{renderLoadingBars(rows)}</React.Fragment>;
  };
};

export default PostLoading;
