import React, { Component } from "react";
import axiosInstance from "axios";
import apiURL from "../../apiUrl";
import UserContext from "../../context/userContext";
import DarkContext from "./../../context/darkMode";

export default class PostDetail extends Component {
  static contextType = UserContext;

  state = {
    post: {},
  };

  componentDidMount() {
    axiosInstance
      .get(apiURL + this.props.match.params.pk)
      .then((res) => this.setState({ post: res.data }));
  }

  render() {
    const { title, author, content, status, published } = this.state.post;

    return (
      <article className="post-container">
        <div className="post-img-title">
          <img
            src="http://source.unsplash.com/random"
            className="card-img-top"
            alt={"Image for: " + title}
          />
          <h1>{title}</h1>
        </div>

        <span>
          posted by{" "}
          <p>{author ? this.context.getAuthor(author) : "Anonymous"}</p>
          <p>
            {published
              ? new Intl.DateTimeFormat("en-GB", {
                  dateStyle: "full",
                }).format(new Date(published))
              : "no time"}
          </p>
        </span>

        <hr />

        <div className="post-content">content={content}</div>
      </article>
    );
  }
}
