import React, { Component } from "react";
import { Link } from "react-router-dom";
import UserContext from "./../../context/userContext";
import ReactTooltip from "react-tooltip";
import axiosInstance from "./../../baseAxios";

export default class SingularPost extends Component {
  state = { rating: 0 };

  likeDislike(post, action) {
    if (action === "like") {
      const postRate = parseInt(post.rating);
      console.log(postRate, post.slug);

      const restPost = {
        title: post.title,
        slug: post.slug,
        content: post.content,
        author: post.author,
      };

      console.log(restPost);
      axiosInstance
        .put("update-post/" + post.slug, { ...restPost, rating: postRate + 1 })
        .then((res) => console.log(res.data))
        .catch((res) => console.log(res.response));
    }
  }
  render() {
    const {
      title,
      content,
      slug,
      author,
      image,
      rating,
      postDate,
      day,
      dataType,
      post,

      getFormattedRating,
    } = this.props;
    return (
      <div
        className={
          this.context.darkMode
            ? "blog-post text-light border bg-dark"
            : "blog-post border"
        }
        key={post.id}
      >
        <ReactTooltip />
        <div className="blog-post-img border">
          <img src={image} alt={"Image for: " + title} />
        </div>
        <div className="blog-post-info">
          <div className="blog-post-date">
            <span>{day}</span>
            <span>{postDate}</span>
          </div>
          <div className="blog-post-author">
            <UserContext.Consumer>
              {(userContext) => {
                return (
                  <React.Fragment>
                    by <span>{userContext.getAuthor(author)}</span>
                  </React.Fragment>
                );
              }}
            </UserContext.Consumer>
          </div>

          <h1 className="blog-post-title">{title}</h1>
          <p className="blog-post-text">
            {content.length >= 500
              ? content.substring(0, 500) + "..."
              : content}
          </p>
          <Link to={"posts/" + slug} className="blog-post-cta">
            Read More
          </Link>
        </div>
        <div className="blog-post-options">
          <div className="react">
            <div className="rating badge badge-primary">
              <span
                data-tip={
                  "rating: " +
                  rating.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",")
                }
                data-type={dataType}
              >
                {rating ? getFormattedRating(rating) : 0}
              </span>
            </div>
            <div className="upvote">
              <button
                className="btn btn-success"
                data-tip="like"
                data-type={dataType}
                onClick={() => this.likeDislike(post, "like")}
              >
                <i className="fas fa-thumbs-up"></i>
              </button>
            </div>
            <div className="downvote">
              <button
                className="btn btn-danger"
                data-tip="dislike"
                data-type={dataType}
              >
                <i className="fas fa-thumbs-down"></i>
              </button>
            </div>
          </div>
          <div className="other-react">
            <div className="bookmark">
              <button
                className="btn btn-info"
                data-tip="add to bookmarks"
                data-type={dataType}
              >
                <i className="far fa-bookmark"></i>
                {/* <i className="fas fa-bookmark"></i> */}
              </button>
            </div>
            <div
              className="comment"
              data-tip="comment on this post"
              data-type={dataType}
            >
              <button className="btn btn-dark">
                <i
                  style={{ fontSize: ".8rem" }}
                  className="far fa-comment-alt"
                ></i>
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
