import React from "react";
import DarkContext from "../../context/darkMode";

const SearchBar = ({ handleChange, placeholder, searchQuery }) => {
  return (
    <DarkContext.Consumer>
      {(darkContext) => (
        <input
          className={
            "form-control " +
            `${darkContext.darkMode ? "bg-dark text-light" : ""}`
          }
          type="search"
          placeholder={placeholder}
          value={searchQuery}
          aria-label="Search"
          onChange={handleChange}
        />
      )}
    </DarkContext.Consumer>
  );
};

export default SearchBar;
