import apiURL from "../apiUrl";
import { axios } from "axios";

const userListURL = apiURL + "user/list/";

const getUserById = (pk) => {
  let username;
  axios.get(userListURL + pk).then((res) => (username = res.data));
  return username;
};

export default getUserById;
