import React, { Component } from "react";
import { Link } from "react-router-dom";
import DarkContext from "../../context/darkMode";
import UserPostsWrapper from "./userPostsWrapper";
import axiosInstance from "./../../baseAxios";

export default class Dashboard extends Component {
  state = {
    currentUserId: JSON.parse(
      atob(localStorage.getItem("access_token").split(".")[1])
    ).user_id,
    currentUser: {},
  };

  componentDidMount() {
    axiosInstance
      .get("user/list/" + this.state.currentUserId)
      .then((res) => this.setState({ currentUser: res.data }));
  }

  renderData = (reaction) => {
    const { currentUser } = this.state;

    console.log("waaawS");

    if (currentUser[reaction]) {
      console.log(currentUser[reaction]);
      return currentUser[reaction].map((l) => <p>{l}</p>);
    }
    return "";
  };

  render() {
    return (
      <DarkContext.Consumer>
        {(darkContext) => (
          <div className="dashboard">
            <div
              className={
                darkContext.darkMode
                  ? "user-info dark-page-shadow bg-dark text-light"
                  : "user-info border bg-light"
              }
            >
              <h4>
                Sort of user profile for {localStorage.getItem("currentUser")}
              </h4>
            </div>
            <div
              className={
                darkContext.darkMode
                  ? "posts-reacted dark-page-shadow bg-dark text-light"
                  : "posts-reacted border bg-light"
              }
            >
              <div className="accordion">
                <button className="border">
                  <span>likes</span> <i className="fas fa-chevron-down"></i>
                </button>
                <div className="data">{() => this.renderData("liked")}</div>
              </div>
              <div className="accordion">
                <button className="border">
                  <span> dislikes</span>
                  <i className="fas fa-chevron-down"></i>
                </button>
                <div className="data">{() => this.renderData("disliked")}</div>
              </div>
              <div className="accordion">
                <button className="border">
                  <span>bookmarks</span>
                  <i className="fas fa-chevron-down"></i>
                </button>
                <div className="data">
                  {() => this.renderData("bookmarked")}
                </div>
              </div>
            </div>

            <div className="user-posts">
              <div className="manage-user-posts">
                <Link to="/create-post">
                  Post <i className="fas fa-plus-square"></i>
                </Link>
              </div>
              <UserPostsWrapper
                toggleShowSearchBar={this.props.toggleShowSearchBar}
              ></UserPostsWrapper>
            </div>
          </div>
        )}
      </DarkContext.Consumer>
    );
  }
}
