import React from "react";
import Skeleton from "@yisheng90/react-loading";

/*<Spinner animation="grow" variant="primary" />*/

const PostLoading = (Component) => {
  return function PostLoadingComponent({ items, isLoading, ...props }) {
    if (!isLoading) {
      return <Component {...props} />;
    }

    return (
      <React.Fragment>
        {items === "1+" ? (
          <React.Fragment>
            <Skeleton height={220} />
            <Skeleton height={220} />
          </React.Fragment>
        ) : (
          <Skeleton height={220} width={"80%"} />
        )}
      </React.Fragment>
    );
  };
};

export default PostLoading;
