import React from "react";
import axios from "axios";
import Posts from "./posts";
import PostLoadingComponent from "../actions/postLoading";
import { useState, useEffect, useContext } from "react";
import apiURL from "../../apiUrl";
import DarkContext from "./../../context/darkMode";
import { useHistory } from "react-router-dom";

const Body = ({ toggleShowSearchBar }) => {
  const darkContext = useContext(DarkContext);
  let history = useHistory();

  const PostLoading = PostLoadingComponent(Posts);
  const [appState, setAppState] = useState({ loading: false, posts: null });

  useEffect(() => {
    toggleShowSearchBar(true);

    setAppState({ loading: true });
    axios
      .get(apiURL)
      .then((res) => setAppState({ loading: false, posts: res.data }));

    axios.get(apiURL + "search/?search=s").then((res) => console.log(res.data));
  }, [setAppState]);

  /*const handleChange = (e) => {
    e.preventDefault();
    setSearchValue({ search: e.target.value });
    console.log(searchValue.search);
  };

  const goSearch = (e) => {
    history.push({
      pathname: "/search/",
      search: "?search= " + searchValue.search,
    });
    window.location.reload();
  };


   <SearchBar
     handleChange={handleChange}
     placeholder="search by title"
     searchQuery={searchValue.search}
     setAppState={setAppState}
     onRequestSearch={() => goSearch(searchValue.search)}
   />; */

  return (
    <main
      className={
        darkContext.darkMode
          ? "bg-dark main-dark rm-box-shadow"
          : "bg-light border"
      }
    >
      <div className="post-tags">
        <span
          className={
            darkContext.darkMode ? "badge badge-light" : "badge badge-dark"
          }
        >
          latest
        </span>
      </div>

      <PostLoading isLoading={appState.loading} posts={appState.posts} />
    </main>
  );
};

export default Body;
