import React, { Component } from "react";
import Form from "./../common/form";
import Joi from "./../../utils/extendedJoi";

export default class UserInfo extends Form {
  state = {
    data: { bio: "" },
    errors: {},

    pageBio: "",

    bioChangeActive: false,
  };

  schema = {
    bio: Joi.string().required().min(20).label("Bio"),
  };

