import React, { useContext } from "react";
import { Link } from "react-router-dom";
import UserContext from "../../context/userContext";
import DarkContext from "./../../context/darkMode";

const Posts = ({ posts }) => {
  const userContext = useContext(UserContext);
  const darkContext = useContext(DarkContext);

  if (posts) {
    console.log("there are posts");
  }

  return (
    <section className="posts-section">
      {posts &&
        posts.map((post) => (
          <div
            className={darkContext.darkMode ? "card-dark" : "card"}
            key={post.id}
          >
            <img
              src="http://source.unsplash.com/random"
              className="card-img-top"
              alt={"Image for: " + post.title}
            />
            <div
              className={
                darkContext.darkMode
                  ? "card-body bg-dark text-light"
                  : '"card-body bg-light text-dark"'
              }
            >
              <h5 className="card-title text-info">
                <b>{post.title}</b>
              </h5>
              <p>
                <span
                  className={
                    darkContext.darkMode
                      ? "date-posted text-light"
                      : "date-posted text-dark"
                  }
                >
                  <i>
                    {new Intl.DateTimeFormat("en-GB", {
                      dateStyle: "full",
                    }).format(new Date(post.published))}
                  </i>
                </span>
                <br />
                <span
                  className={darkContext.darkMode ? "text-light" : "text-dark"}
                >
                  <b>
                    {post.author
                      ? userContext.getAuthor(post.author)
                      : "Anonymous"}
                  </b>
                </span>
              </p>
              <p className="card-text">
                {post.excerpt.substring(0, 150) + "..."}
              </p>
              <Link to={"/post/" + post.slug} className="btn btn-info">
                more...
              </Link>
            </div>
          </div>
        ))}
    </section>
  );
};

export default Posts;
