import React, { Component } from "react";
import { Link } from "react-router-dom";
import DarkContext from "../../context/darkMode";
import UserPostsWrapper from "./userPostsWrapper";
import axiosInstance from "./../../baseAxios";

export default class Dashboard extends Component {
  state = {
    currentUserId: JSON.parse(
      atob(localStorage.getItem("access_token").split(".")[1])
    ).user_id,
    currentUser: {},
    posts: null,
  };

  componentDidMount() {
    axiosInstance
      .get("user/list/" + this.state.currentUserId)
      .then((res) => this.setState({ currentUser: res.data }));
    axiosInstance.get().then((res) => this.setState({ posts: res.data }));
  }

  renderPostById = (id) => {
    if (this.state.posts) {
      const post = this.state.posts.filter((post) => post.id === id)[0];
      return post.title;
    }
  };

  getPostSlug = (id) => {
    const url = "posts/" + posts.filter((post) => post.id === id)[0].slug;
    if (url) return url;
    return "";
  };

  render() {
    const { currentUser, posts } = this.state;
    return (
      <DarkContext.Consumer>
        {(darkContext) => (
          <div className="dashboard">
            <div
              className={
                darkContext.darkMode
                  ? "user-info dark-page-shadow bg-dark text-light"
                  : "user-info border bg-light"
              }
            >
              <h4>
                Sort of user profile for {localStorage.getItem("currentUser")}
              </h4>
            </div>
            <div
              className={
                darkContext.darkMode
                  ? "posts-reacted dark-page-shadow bg-dark text-light"
                  : "posts-reacted border bg-light"
              }
            >
              <div className="accordion">
                <button className="border">
                  <span>likes</span> <i className="fas fa-chevron-down"></i>
                </button>
                <div className="data">
                  {currentUser.liked ? (
                    <React.Fragment>
                      {currentUser.liked.map((i) => (
                        <p>
                          <i className="fas fa-thumbs-up"></i>
                          <Link
                            to={
                              "posts/" +
                              posts.filter((post) => post.id === i)[0].slug
                            }
                          >
                            {" "}
                            {this.renderPostById(i)}
                          </Link>
                        </p>
                      ))}
                    </React.Fragment>
                  ) : (
                    ""
                  )}
                </div>
              </div>
              <div className="accordion">
                <button className="border">
                  <span> dislikes</span>
                  <i className="fas fa-chevron-down"></i>
                </button>
                <div className="data">
                  {currentUser.disliked ? (
                    <React.Fragment>
                      {currentUser.disliked.map((i) => (
                        <p>
                          <i className="fas fa-thumbs-up"></i>
                          <Link
                            to={
                              "posts/" +
                              posts.filter((post) => post.id === i)[0].slug
                            }
                          >
                            {" "}
                            {this.renderPostById(i)}
                          </Link>
                        </p>
                      ))}
                    </React.Fragment>
                  ) : (
                    ""
                  )}
                </div>
              </div>
              <div className="accordion">
                <button className="border">
                  <span>bookmarks</span>
                  <i className="fas fa-chevron-down"></i>
                </button>
                <div className="data">
                  {currentUser.bookmarked ? (
                    <React.Fragment>
                      {currentUser.bookmarked.map((i) => (
                        <p>
                          <i className="fas fa-thumbs-up"></i>
                          <Link to={this.getPostSlug(i)}>
                            {" "}
                            {this.renderPostById(i)}
                          </Link>
                        </p>
                      ))}
                    </React.Fragment>
                  ) : (
                    ""
                  )}
                </div>
              </div>
            </div>

            <div className="user-posts">
              <div className="manage-user-posts">
                <Link to="/create-post">
                  Post <i className="fas fa-plus-square"></i>
                </Link>
              </div>
              <UserPostsWrapper
                toggleShowSearchBar={this.props.toggleShowSearchBar}
              ></UserPostsWrapper>
            </div>
          </div>
        )}
      </DarkContext.Consumer>
    );
  }
}
