import React, { useState, useEffect } from "react";
import PostDetail from "./postDetail";
import PostLoadingComponent from "./../actions/postLoading";
import axiosInstance from "axios";
import apiURL from "../../apiUrl";
import { useHistory } from "react-router-dom";

const PostDetailWrapper = ({ toggleShowSearchBar, match }) => {
  const history = useHistory();

  const [appState, setAppState] = useState({
    postIsLoading: false,
    post: {},
  });

    const [comments, setComments] = useState(new Array());

  useEffect(() => {
    toggleShowSearchBar(true);

   
   
  }, [setAppState]);

  return (
    <React.Fragment>
      <PostDetail
       
        comments={comments}
        setComments={setComments}
        
        post={appState.post}
      />
    </React.Fragment>
  );
};

export default PostDetailWrapper;
