import React from "react";
import Skeleton from "@yisheng90/react-loading";

/*<Spinner animation="grow" variant="primary" />*/

const PostLoading = (Component) => {
  const renderLoadingBars = (rows) => {
    switch (rows) {
      case 1:
        return <Skeleton height={220} width={"90%"} />;
      case 2:
        return (
          <React.Fragment>
            <Skeleton height={220} width={"90%"} class="ree" />
            <Skeleton height={220} width={"90%"} class="ree" />
          </React.Fragment>
        );

      case 3:
        return (
          <React.Fragment>
            <Skeleton height={220} width={"90%"} class="ree" />
            <Skeleton height={220} width={"90%"} class="ree" />
            <Skeleton height={220} width={"90%"} class="ree" />
            );
          </React.Fragment>
        );
    }
  };

  return function PostLoadingComponent({ items, isLoading, ...props }) {
    if (!isLoading) {
      return <Component {...props} />;
    }

    return <React.Fragment>{}</React.Fragment>;
  };
};

export default PostLoading;
